-- Generate some reviews
create or replace function generate_ratings() returns void as $$

    declare
        _random_user int;
        _random_rating int;
        _random_comment text;
        _num_ratings_min int = 3;
        _num_ratings_max int = 50;
        _num_ratings int;
        _books_cursor refcursor;
        _record record;
        _book_id varchar(10);
        _book_published_timestamp timestamp without time zone;
        _random_timestamp timestamp without time zone;
    begin
        open _books_cursor for select b.id, b.published from books b where not exists (select * from book_ratings br where br.book_id = b.id);

        loop
            fetch _books_cursor into _record;
            exit when not found;
            _book_id = _record.id;
            _book_published_timestamp = _record.published::timestamp AT TIME ZONE 'UTC';

            -- Generate a random number
            select trunc(random() * _num_ratings_max + _num_ratings_min) into _num_ratings;
            raise notice 'creating % ratings for book %', _num_ratings, _record.id;

            -- Loop through and create that number of ratings
            for i in 1.._num_ratings loop
                -- Who's the lucky user?
                select id from users order by random() limit 1 into _random_user;
                -- What did they rate? (up to 6 since we're flooring down to 5)
                select trunc(random() * 6) into _random_rating;
                -- What did they comment?
                select data from temp_lorem_ipsum order by random() limit 1 into _random_comment;
                -- When did they do it? (sometime between now and when the book was published)
                select _book_published_timestamp + random() * (now() - _book_published_timestamp) into _random_timestamp;

                -- Insert
            end loop;

        end loop;

        close _books_cursor;
    end;

    $$ language plpgsql;

-- Fire away!
select generate_ratings();

-- Delete the temp data
drop table temp_lorem_ipsum;
drop function generate_ratings();